public class Practice06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i < 6; i++) {
			for (int j = 2; j < 6; j++) {
				System.out.print(j + "*" + i + "=" + j * i + "\t");
			}
			System.out.println();
		}
		System.out.println();
		for (int i = 1; i < 6; i++) {
			for (int j = 6; j < 10; j++) {
				System.out.print(j + "*" + i + "=" + j * i + "\t");
			}
			System.out.println();
		}
		
		
	}

}
